(defn parse-input
  ""
  [input]
  (reduce 
    (fn [result value] 
      (conj result (+ (last result) value)))
    [0]
    (map (fn [ch] 
      (if (= (str ch) "(") 1 -1)) input)))

(defn calculate-floor
  ""
  [input]
    (loop [[ch & remaining] input result 0]
      (if (nil? ch)
        result
        (let [strCh (str ch) op (if (= strCh "(") inc dec)]
          (recur remaining (op result))))))

(defn calculate-basement-pos
  ""
  []
  nil)

(defn -main
  "Day 1: Not Quite Lisp"
  []
  (println 
    (str 
      (parse-input input))))
      ;(calculate-floor input)
      ;" - "
      ;(calculate-basement-pos input)))
