(defn calculate-floor
  ""
  [input]
  (reduce 
    (fn [result value] 
      (conj result (+ (last result) value)))
    [0]
    (map (fn [ch] 
      (if (= (str ch) "(") 1 -1)) input)))

(defn calculate-basement-pos
  ""
  [input]
  (.indexOf input -1))

(defn -main
  "Day 1: Not Quite Lisp"
  []
  (println 
    (str 
      (let [
        result (calculate-floor input)
        basement-pos (calculate-basement-pos result)]
        [(last result) basement-pos]))))
